ValidationRule.errorConditionFormula:
AND (
$User.Exempt_from_Validation_Rules__c = False, NOT(ISBLANK(Source_ID__c)), IsChanged(StageName), ISPICKVAL(StageName,'Closed Admin'),
/* disabled for renewal and BoR opportunities */ RecordType.Name != 'Benefits Renewal', RecordType.Name != 'Benefits BoR', /* disabled for BoR PEs */ NOT (CONTAINS($Label.Benefits_Management_Roles , $UserRole.Name + ',')) )
