WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/39.0/connection.js")} 
{!REQUIRESCRIPT("/soap/ajax/39.0/apex.js")} 

try { 

function getDateTime() { 
var now = new Date(); 
var year = now.getUTCFullYear(); 
var month = now.getUTCMonth()+1; 
var day = now.getUTCDate(); 
var hour = now.getUTCHours(); 
var minute = now.getUTCMinutes(); 
var second = now.getUTCSeconds(); 
if(month.toString().length == 1) { 
var month = '0'+month; 
} 
if(day.toString().length == 1) { 
var day = '0'+day; 
} 
if(hour.toString().length == 1) { 
var hour = '0'+hour; 
} 
if(minute.toString().length == 1) { 
var minute = '0'+minute; 
} 
if(second.toString().length == 1) { 
var second = '0'+second; 
} 
var dateTime = year+'-'+month+'-'+day+'T'+hour+':'+minute+':'+second+'Z'; 
return dateTime; 
} 

function getDueDate(dueDate) { 
var dDate = new Date(dueDate); 
var year = dDate.getUTCFullYear(); 
var month = dDate.getUTCMonth()+1; 
var day = dDate.getUTCDate(); 
if(month.toString().length == 1) { 
var month = '0'+month; 
} 
if(day.toString().length == 1) { 
var day = '0'+day; 
} 
var date = year+'-'+month+'-'+day; 
return date; 
} 

var update_Task = new sforce.SObject("Task"); 
update_Task.ID = '{!Task.Id}'; 
var recRecordTypeId = '{!Task.RecordTypeId}'; 
var recWhatId = '{!Benefit_Order__c.Id}'; 
var carrierWhatId = '{!Carrier_Order__c.Id}'; 
var recDueDate = '{!Task.ActivityDate}'; 
var completedStatus = 'Completed'; 
var omittedStatus = 'Omitted'; 
var dueDate = getDueDate(recDueDate); 


if(carrierWhatId){ 
recWhatId = carrierWhatId; 
} 
var nextRecordId; 

var queryRecords = sforce.connection.query("Select Id from Task where ActivityDate >=" + dueDate+" and WhatId = '"+recWhatId+"' and Id != '{!Task.Id}' and Status != '"+completedStatus+"' and Status != '"+omittedStatus+"' and RecordTypeId = '"+ recRecordTypeId+"' ORDER BY ActivityDate ASC"); 
var records = queryRecords.getArray("records"); 
if(records[0]!=null){ 
nextRecordId =records[0].Id; 
} 

update_Task.Status = 'Completed'; 
update_Task.Date_Completed__c = getDateTime(); 
result = sforce.connection.update([update_Task]); 
//window.location.reload(); 
if(nextRecordId!=null){ 
window.location='/'+nextRecordId; 
} else{ 
window.location='/'+recWhatId; 
} 
} 
catch(er) { 
alert('Oops! There was an error.\n'+er); 
}
