WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/31.0/connection.js")}

try{

    var TicketToUpdate = new sforce.SObject("Ticket__c");

    TicketToUpdate.Id = "{!Ticket__c.Id}";

    TicketToUpdate.OwnerId = "{!$User.Id}";

    var result = sforce.connection.update([TicketToUpdate]);

    if(result[0].success == "true"){

        location.reload();

    }

    else{

        alert(

            "An Error has Occurred. Error: " + 

            result[0].errors.message

        );

    }

}

catch(e){

    alert(

        "An Error has Occurred. Error: " + 

        e

    );

}
